// src/components/Urenregistratie.js
import React, { useState } from 'react';
import './Urenregistratie.css';
import UrenregistratieForm from './UrenregistratieForm';
import UrenregistratieTable from './UrenregistratieTable';

function Urenregistratie() {
  // State voor de opgeslagen urenregistraties
  const [urenRegistraties, setUrenRegistraties] = useState([]);

  // Functie om nieuwe registratie toe te voegen
  const handleAddRegistratie = (nieuweRegistratie) => {
    setUrenRegistraties([...urenRegistraties, nieuweRegistratie]);
  };

  return (
    <div className="urenregistratie">
      <h1>Urenregistratie Systeem</h1>
      <UrenregistratieForm onAddRegistratie={handleAddRegistratie} />
      <UrenregistratieTable urenRegistraties={urenRegistraties} />
    </div>
  );
}

export default Urenregistratie;
