import React, { useState } from 'react';
import './Urenregistratie.css';

const Urenregistratie = () => {
  const [registraties, setRegistraties] = useState([]);
  const [formData, setFormData] = useState({
    datum: '',
    starttijd: '',
    eindtijd: '',
    prijs: '',
    uur: '',
    reiskosten: '',
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevFormData) => ({
      ...prevFormData,
      [name]: value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Bereken totaal aantal uren op basis van starttijd en eindtijd
    const start = new Date(`${formData.datum}T${formData.starttijd}`);
    const end = new Date(`${formData.datum}T${formData.eindtijd}`);
    const uren = (end - start) / 1000 / 3600;

    const nieuweRegistratie = {
      datum: new Date(formData.datum),
      starttijd: formData.starttijd,
      eindtijd: formData.eindtijd,
      prijs: parseFloat(formData.prijs),
      uur: uren,
      reiskosten: parseFloat(formData.reiskosten),
      totaal: uren * parseFloat(formData.prijs) + parseFloat(formData.reiskosten),
    };

    setRegistraties([...registraties, nieuweRegistratie]);
    setFormData({
      datum: '',
      starttijd: '',
      eindtijd: '',
      prijs: '',
      uur: '',
      reiskosten: '',
    });
  };

  // Bereken het totaal van alle registraties
  const totaalPrijs = registraties.reduce((acc, curr) => acc + curr.totaal, 0);

  const formatDate = (date) => {
    return date.toLocaleDateString('nl-NL', {
      day: '2-digit',
      month: 'long',
      year: 'numeric',
    });
  };

  return (
    <div className="urenregistratie">
      <h2>Urenregistratie</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Datum:
          <input
            type="date"
            name="datum"
            value={formData.datum}
            onChange={handleInputChange}
            required
          />
        </label>
        <label>
          Starttijd:
          <input
            type="time"
            name="starttijd"
            value={formData.starttijd}
            onChange={handleInputChange}
            required
          />
        </label>
        <label>
          Eindtijd:
          <input
            type="time"
            name="eindtijd"
            value={formData.eindtijd}
            onChange={handleInputChange}
            required
          />
        </label>
        <label>
          Prijs per uur:
          <input
            type="number"
            name="prijs"
            value={formData.prijs}
            onChange={handleInputChange}
            required
          />
        </label>
        <label>
          Reiskosten:
          <input
            type="number"
            name="reiskosten"
            value={formData.reiskosten}
            onChange={handleInputChange}
            required
          />
        </label>
        <button type="submit">Toevoegen</button>
      </form>

      <h3>Registraties</h3>
      <ul>
        {registraties.map((registratie, index) => (
          <li key={index}>
            {formatDate(registratie.datum)} | {registratie.starttijd} - {registratie.eindtijd} | Uren: {registratie.uur.toFixed(2)} | Prijs: €{registratie.prijs} | Reiskosten: €{registratie.reiskosten} | Totaal: €{registratie.totaal.toFixed(2)}
          </li>
        ))}
      </ul>

      <h3>Totaal: €{totaalPrijs.toFixed(2)}</h3>
    </div>
  );
};

export default Urenregistratie;
