// src/App.js
import React, { useState } from 'react';
import './App.css';
import UrenregistratieForm from './components/UrenregistratieForm';

function App() {
  const [registraties, setRegistraties] = useState([]);

  const handleAddRegistratie = (nieuweRegistratie) => {
    setRegistraties((prevRegistraties) => [...prevRegistraties, nieuweRegistratie]);
  };

  const berekenTotaalAlleRegistraties = () => {
    return registraties.reduce((totaal, registratie) => totaal + parseFloat(registratie.totaal), 0).toFixed(2);
  };

  return (
    <div className="App">
      <h1>Urenregistratie Systeem</h1>
      <UrenregistratieForm onAddRegistratie={handleAddRegistratie} />

      <h2>Geregistreerde Uren</h2>
      <table>
        <thead>
          <tr>
            <th>Datum</th>
            <th>Starttijd</th>
            <th>Eindtijd</th>
            <th>Uren</th>
            <th>Prijs per uur</th>
            <th>Reiskosten</th>
            <th>Totaal</th>
          </tr>
        </thead>
        <tbody>
          {registraties.map((registratie, index) => (
            <tr key={index}>
              <td>{registratie.datum}</td>
              <td>{registratie.starttijd}</td>
              <td>{registratie.eindtijd}</td>
              <td>{registratie.uren}</td>
              <td>€ {registratie.prijsPerUur}</td>
              <td>€ {registratie.reiskosten}</td>
              <td>€ {registratie.totaal}</td>
            </tr>
          ))}
        </tbody>
      </table>

      <h3>Totaal bedrag van alle registraties: € {berekenTotaalAlleRegistraties()}</h3>
    </div>
  );
}

export default App;
