// src/components/UrenregistratieForm.js
import React, { useState } from 'react';

function UrenregistratieForm({ onAddRegistratie }) {
  const [datum, setDatum] = useState('');
  const [starttijd, setStarttijd] = useState('');
  const [eindtijd, setEindtijd] = useState('');
  const [prijsPerUur, setPrijsPerUur] = useState('');
  const [reiskosten, setReiskosten] = useState('');

  const berekenUren = () => {
    const start = new Date(`1970-01-01T${starttijd}:00`);
    const end = new Date(`1970-01-01T${eindtijd}:00`);
    const verschil = (end - start) / (1000 * 60 * 60);
    return verschil > 0 ? verschil : 0;
  };

  const berekenTotaalBedrag = () => {
    const uren = berekenUren();
    const prijs = parseFloat(prijsPerUur) || 0;
    const reis = parseFloat(reiskosten) || 0;
    return (uren * prijs + reis).toFixed(2);
  };

  // Functie om de datum te formatteren naar dd-mm-yyyy
  const formatDate = (dateString) => {
    const date = new Date(dateString);
    const day = String(date.getDate()).padStart(2, '0');
    const month = String(date.getMonth() + 1).padStart(2, '0');
    const year = date.getFullYear();
    return `${day}-${month}-${year}`;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    
    const nieuweRegistratie = {
      datum: formatDate(datum), // Format datum naar dd-mm-yyyy
      starttijd,
      eindtijd,
      uren: berekenUren(),
      prijsPerUur,
      reiskosten,
      totaal: berekenTotaalBedrag(),
    };
    
    onAddRegistratie(nieuweRegistratie);
    
    // Reset velden
    setDatum('');
    setStarttijd('');
    setEindtijd('');
    setPrijsPerUur('');
    setReiskosten('');
  };

  return (
    <form onSubmit={handleSubmit} className="urenregistratie-form">
      <label>Datum:
        <input type="date" value={datum} onChange={(e) => setDatum(e.target.value)} required />
      </label>
      <label>Starttijd:
        <input type="time" value={starttijd} onChange={(e) => setStarttijd(e.target.value)} required />
      </label>
      <label>Eindtijd:
        <input type="time" value={eindtijd} onChange={(e) => setEindtijd(e.target.value)} required />
      </label>
      <label>Prijs per uur:
        <input type="number" value={prijsPerUur} onChange={(e) => setPrijsPerUur(e.target.value)} required />
      </label>
      <label>Reiskosten:
        <input type="number" value={reiskosten} onChange={(e) => setReiskosten(e.target.value)} />
      </label>
      <button type="submit">Toevoegen</button>
    </form>
  );
}

export default UrenregistratieForm;
